import React, { useState, useEffect } from "react";
import styles from "./AppList.module.css";
import NavBar from "../../components/NavBar/NavBar";
import SearchBar from "../../components/SearchBar/SearchBar.jsx";
import Box from "../../components/Box/Box";
import Pagination from "../../components/Pagination/Pagination";
import NavLink from "../../components/NavLink/NavLink";
import { Switch, useRouteMatch, useParams, Route } from "react-router-dom";
import data from "../../data/apps.json";

const sortBySumPriceAsc = appsArray => {
  return (
    appsArray &&
    appsArray.sort((a, b) => {
      let sumPriceA = 0;
      let sumPriceB = 0;
      a.subscriptions.map(subscription => (sumPriceA += subscription.price));
      b.subscriptions.map(subscription => (sumPriceB += subscription.price));
      return (sumPriceA > sumPriceB && 1) || sumPriceA === sumPriceB ? 0 : -1;
    })
  );
};

const filterByAppName = (app, name) => {
  if (app.name.length > 0) {
    return app.name.toLowerCase().includes(name.toLowerCase());
  } else return 1;
};

const FilteredApps = ({ category, name }) => {
  //const [apps] = sortPriceAsc(filterByName(filterByCategory(useApps())));
  const [apps] = useApps(category, name);

  sortBySumPriceAsc(apps);
  //apps.filter(app => app.name.toLowerCase().includes(name.toLowerCase()));
  //console.log(apps)
  return apps.length === 0 ? (
    <h1>No results were found!</h1>
  ) : (
    apps.map(app => (
      <Box
        key={app.id}
        title={app.name}
        description={app.description}
        tags={app.categories}
        subscriptions={app.subscriptions}
      ></Box>
    ))
  );
};

const useCategories = () => {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    setCategories(
      data
        .map(item => item.categories)
        .reduce((p, c) => [...p, ...c])
        .filter((item, i, arr) => arr.indexOf(item) === i)
        .sort()
    );
  }, []);
  return [categories];
};

//Get current items
const indexOfLastAppItem = currentPage * itemsPerPage;
const indexOfFirstAppItem = indexOfLastAppItem - itemsPerPage;
const currentAppItems = appList.slice(indexOfFirstAppItem, indexOfLastAppItem);

const useApps = (category, name) => {
  const [apps, setApps] = useState([]);

  useEffect(() => {
    if (category) {
      setApps(
        data.filter(app => {
          return (
            app.categories.includes(category) && filterByAppName(app, name)
          );
        })
      );
    } else setApps(data.filter(app => filterByAppName(app, name)));
  }, [category, name]);

  return [apps];
};



const AppList = () => {
  const match = useRouteMatch();
  const [categories] = useCategories();
  const [searchFilter, setSearchFilter] = useState("");

  return (
    <div className={styles.container}>
      <NavBar title="Categories">
        <NavLink name="All Categories" to={`${match.path}`}></NavLink>
        {categories &&
          categories.map((category, i) => (
            <NavLink
              key={i}
              name={category}
              to={`${match.path}${category}`}
            ></NavLink>
          ))}
      </NavBar>

      <section className={styles.appList}>
        <header>
          <SearchBar onValueChange={setSearchFilter} />
        </header>
        <ul>
          <Switch>
            <Route
              path={`${match.path}:category`}
              render={props => (
                <FilteredApps
                  category={props.match.params.category}
                  name={searchFilter}
                />
              )}
            />
            <Route
              path={`${match.path}`}
              render={props => <FilteredApps name={searchFilter} />}
            />
          </Switch>
        </ul>
        <Pagination />
      </section>
    </div>
  );
};

export default AppList;
