import React, { useState, useEffect } from 'react';
import styles from './Pagination.module.css';
import { Link, useRouteMatch } from 'react-router-dom';

const returnObj = (i) => {
  return { text: i, active: false };
};

const createPaginationNumbers = howMany => {
  let paginationNumbers = [];
  for (let i = 1; i <= howMany; i++){
    paginationNumbers.push(returnObj(i))
  }
  return paginationNumbers;

};

const setOneActive = (paginationNumbers, activePage) => {
  return paginationNumbers.map(p =>
    p.text === activePage
      ? { text: p.text, active: true }
      : { text: p.text, active: false }
  );
};

const setPreviousActive = () => {

 }

const Pagination = ({ numberOfPages, activePage }) => {
  //const [currentPage, setCurrentPage] = useState(1);
  const [paginationNumbers, setPaginationNumbers] = useState(
    setOneActive(createPaginationNumbers(numberOfPages || 10),activePage || 1)
  );
  /*const maxPages = Math.ceil(totalItems / itemsPerPage);
  for (let index = 1; index <= maxPages; index++) {
    pageNumbers.push(index);
  }*/


  return (
    <ul className={styles.pagination}>
      <li>
        <a
          href='!#'
          onClick={() =>
            setPaginationNumbers(setOneActive(paginationNumbers, ))
          }
        >
          &lt;
        </a>
      </li>
      {paginationNumbers.map(number => (
        <li
          className={number.active ? styles.active : 'false'}
          key={number.text}
        >
          <a
            href='!#'
            onClick={() =>
              setPaginationNumbers(setOneActive(paginationNumbers, number.text))
            }
          >
            {number.text}
          </a>
        </li>
      ))}

      <li>
        <a href='!#'>&gt;</a>
      </li>
    </ul>
  );
};

export default Pagination;
