import React, { useEffect } from 'react';
import styles from './Pagination.module.css';
import { usePaginationNumbers } from './hooks'
import { getActivePaginationNumber, setOneActive} from './helpers'


const Pagination = ({ numberOfPages, activePage, onPageChange }) => {
  const [paginationNumbers, setPaginationNumbers] = usePaginationNumbers(numberOfPages, activePage);

  useEffect(() => {
    const activePage = getActivePaginationNumber(paginationNumbers);
    activePage && onPageChange && onPageChange(activePage.text);
  }, [paginationNumbers, onPageChange]);

  return (
    <ul className={styles.pagination}>
      <li>
        <a
          onClick={() =>
            setPaginationNumbers(
              setOneActive(
                paginationNumbers,
                getActivePaginationNumber(paginationNumbers).text - 1
              )
            )
          }
          href={'#page=' + activePage}
        >
          &lt;
        </a>
      </li>
      {paginationNumbers.map(number => (
        <li
          className={number.active ? styles.active : 'false'}
          key={number.text}
        >
          <a
            href={'#page=' + number.text}
            onClick={() =>
              setPaginationNumbers(setOneActive(paginationNumbers, number.text))
            }
          >
            {number.text}
          </a>
        </li>
      ))}

      <li>
        <a
          onClick={() =>
            setPaginationNumbers(
              setOneActive(
                paginationNumbers,
                getActivePaginationNumber(paginationNumbers).text + 1
              )
            )
          }
          href={'#page=' + activePage+1}
        >
          &gt;
        </a>
      </li>
    </ul>
  );
};

export default Pagination;
