import React, { useState, useEffect } from "react";
import styles from "./AppList.module.css";
import NavBar from "../../components/NavBar/NavBar";
import SearchBar from "../../components/SearchBar/SearchBar.jsx";
import Box from "../../components/Box/Box";
import Pagination from "../../components/Pagination/Pagination";
import NavLink from '../../components/NavLink/NavLink';
import { Switch, useRouteMatch, useParams, Route } from "react-router-dom";
import data from '../../data/apps.json';

const Filter = ({ category }) => {
  const [apps] = useApps(category);
  return (
    apps &&
    apps.map(app => (
      <Box
        key={app.id}
        title={app.name}
        description={app.description}
        tags={app.categories}
        subscriptions={app.subscriptions}
      ></Box>
    ))
  );
}

const useCategories = () => {
  const [categories, setCategories] = useState([]);

  useEffect(() => { 
      setCategories(
        data.map(item => item.categories).reduce((p, c) => [...p, ...c]).filter((item, i, arr) => arr.indexOf(item) === i)
        .sort()
      )   
  }, [])
  return [categories]
 }

const useApps = (category) => { 
  const [apps, setApps] = useState([]);
  useEffect(() => {
    category ? setApps(data.filter(app => app.categories.includes(category))) : setApps(data);
  }, [category]);
  return [apps];
}
const AppList = () => {
  const match = useRouteMatch();

  const [categories] = useCategories();
  const [searchFilter, setSearchFilter] = useState()
  return (
    <div className={styles.container}>
      <NavBar title="Categories">
        <NavLink name="All Categories" to={`${match.url}`}></NavLink>
        {categories &&
          categories.map((category, i) => (
            <NavLink
              key={i}
              name={category}
              to={`${match.url}/${category}`}
            ></NavLink>
          ))}
      </NavBar>

      <section className={styles.appList}>
        <header>
          <SearchBar onValueChange={console.log} />
        </header>
        <ul>
          <Switch>
            <Route
              path={`${match.path}/:category`}
              render={props => (
                  <Filter category={props.match.params.category} />
              )}
            />
            <Route
              path={`${match.path}`}
              render={props => (
                  <Filter/>
              )}
            />
          </Switch>
        </ul>
        <Pagination />
      </section>
    </div>
  );
};

export default AppList;
