import React, { useState, useEffect } from "react";
import styles from "./Pagination.module.css";
import { Link, useRouteMatch } from "react-router-dom";

const createPaginationNumbers = howMany => {
  let paginationNumbers = [];
  paginationNumbers.length = howMany;
  //Object.seal(paginationNumbers);

  const returnObj = (e,i) => { return { text: ++i, active: false } }
  console.log(returnObj(null,1))
  const x = paginationNumbers.map(returnObj);
};

const setActive = (paginationNumbers, activePage) => {
  return paginationNumbers.map(p => p.text === activePage ? { text: activePage, active: true } : p); 
}

const Pagination = ({ numberOfPages, activePage }) => {
  //const [currentPage, setCurrentPage] = useState(1);
  const [paginationNumbers, setPaginationNumbers] = useState(createPaginationNumbers(numberOfPages || 10));

  console.log(paginationNumbers)
  /*const maxPages = Math.ceil(totalItems / itemsPerPage);
  for (let index = 1; index <= maxPages; index++) {
    pageNumbers.push(index);
  }*/

  const handleClick = number => {
    const updatedNumber = { ...number, active: true };
    setPaginationNumbers([
      ...paginationNumbers.filter(p => p.text !== updatedNumber.text),
      updatedNumber
    ]);

    console.log(paginationNumbers);
  };

  return (
    <ul className={styles.pagination}>
      <li>
        <a href="!#">&lt;</a>
      </li>
      {paginationNumbers.map(number => (
        <li className={number.active && styles.active} key={number.text}>
          <a href="!#" onClick={() => handleClick(number)}>
            {number.text}
          </a>
        </li>
      ))}

      <li>
        <a href="!#">&gt;</a>
      </li>
    </ul>
  );
};

export default Pagination;
