import React, { useState, useEffect } from 'react';
import styles from './Pagination.module.css';
import { Link, useRouteMatch } from 'react-router-dom';

const returnObj = (i) => {
  return { text: i, active: false };
};

const createPaginationNumbers = howMany => {
  let paginationNumbers = [];
  for (let i = 1; i <= howMany; i++){
    paginationNumbers.push(returnObj(i))
  }
  return paginationNumbers;

};

const setActive = (paginationNumbers, activePage) => {
  return paginationNumbers.map(p =>
    p.text === activePage ? { text: activePage, active: true } : p
  );
};

const Pagination = ({ numberOfPages, activePage }) => {
  //const [currentPage, setCurrentPage] = useState(1);
  const [paginationNumbers, setPaginationNumbers] = useState(
    setActive(createPaginationNumbers(numberOfPages || 10),activePage || 1)
  );
  /*const maxPages = Math.ceil(totalItems / itemsPerPage);
  for (let index = 1; index <= maxPages; index++) {
    pageNumbers.push(index);
  }*/

  const handleClick = number => {
    const updatedNumber = { ...number, active: true };
    setPaginationNumbers([
      ...paginationNumbers.filter(p => p.text !== updatedNumber.text),
      updatedNumber
    ]);
  };

  return (
    <ul className={styles.pagination}>
      <li>
        <a href='!#'>&lt;</a>
      </li>
      {paginationNumbers.map(number => (
        <li className={number.active ? styles.active : 'false'} key={number.text}>
          <a href='!#' onClick={() => setPaginationNumbers(setActive(paginationNumbers,))}>
            {number.text}
          </a>
        </li>
      ))}

      <li>
        <a href='!#'>&gt;</a>
      </li>
    </ul>
  );
};

export default Pagination;
