import React, { useState, useEffect } from 'react';
import styles from './Pagination.module.css';
import { act } from 'react-dom/test-utils';

const returnObj = i => {
  return { text: i, active: false };
};

const createPaginationNumbers = howMany => {
  let paginationNumbers = [];
  for (let i = 1; i <= howMany; i++) {
    paginationNumbers.push(returnObj(i));
  }
  return paginationNumbers;
};

const setOneActive = (paginationNumbers, activePage) => {
  if(!activePage) return [...paginationNumbers]
  return paginationNumbers.map(p =>
    p.text === activePage
      ? { text: p.text, active: true }
      : { text: p.text, active: false }
  );
};

const getActivePaginationNumber = (paginationNumbers) => {
  return paginationNumbers.find(p=>p.active)
};

const usePaginationNumbers = (numPages, activePage) => {
  const [paginationNumbers, setPaginationNumbers] = useState([]);
  useEffect(() => {
    setPaginationNumbers(
      setOneActive(createPaginationNumbers(numPages), activePage > numPages? numPages : activePage)
    );
  }, [numPages, activePage]);

  return [paginationNumbers, setPaginationNumbers]
}

const Pagination = ({ numberOfPages, activePage, onPageChange }) => {
  //const [currentPage, setCurrentPage] = useState(1);
  console.log(numberOfPages, activePage, onPageChange);
  const [paginationNumbers, setPaginationNumbers] = usePaginationNumbers(numberOfPages, activePage);
  
  /*const maxPages = Math.ceil(totalItems / itemsPerPage);
  for (let index = 1; index <= maxPages; index++) {
    pageNumbers.push(index);
  }*/
  useEffect(() => {
    const activePage = getActivePaginationNumber(paginationNumbers);
    activePage && onPageChange && onPageChange(activePage.text);
  }, )

  return (
    <ul className={styles.pagination}>
      <li>
        <a
          href='!#'
          onClick={() =>
            setPaginationNumbers(
              setOneActive(
                paginationNumbers,
                getActivePaginationNumber(paginationNumbers).text - 1
              )
            )
          }
        >
          &lt;
        </a>
      </li>
      {paginationNumbers.map(number => (
        <li
          className={number.active ? styles.active : 'false'}
          key={number.text}
        >
          <a
            href='!#'
            onClick={() =>
              setPaginationNumbers(setOneActive(paginationNumbers, number.text))
            }
          >
            {number.text}
          </a>
        </li>
      ))}

      <li>
        <a
          href='!#'
          onClick={() =>
            setPaginationNumbers(
              setOneActive(
                paginationNumbers,
                getActivePaginationNumber(paginationNumbers).text + 1
              )
            )
          }
        >
          &gt;
        </a>
      </li>
    </ul>
  );
};

export default Pagination;
