import React, { useState, useEffect } from 'react';
import data from '../../data/apps.json';

export const useCategories = () => {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    setCategories(
      data
        .map(item => item.categories)
        .reduce((p, c) => [...p, ...c])
        .filter((item, i, arr) => arr.indexOf(item) === i)
        .sort()
    );
  }, []);
  return [categories];
};
