    <Box
      title="Ttile"
      description="descs"
      tags={[category]}
      subscriptions={[
        {
          name: "teste",
          price: 2500
        }
      ]}
    ></Box>
  );
}

const useCategories = () => {
  const [categories, setCategories] = useState([]);

  useEffect(() => { 
      setCategories(
        data.map(item => item.categories).reduce((p, c) => [...p, ...c]).filter((item, i, arr) => arr.indexOf(item) === i)
        .sort()
      )   
  }, [])
  return [categories]
 }

const useApps = () => { 
  const [apps, setApps] = useState([]);
  useEffect(() => { 
    setApps(data);
  }, [])
  return [apps];
}
const AppList = () => {
  const match = useRouteMatch();

  const [categories] = useCategories();

  return (
    <div className={styles.container}>
      <NavBar title="Categories">
        <NavLink name="All Categories" to={`${match.url}`}></NavLink>
        {categories &&
          categories.map((category,i) => (
            <NavLink key={i} name={category} to={`${match.url}/${category}`}></NavLink>
          ))}
      </NavBar>

      <section className={styles.appList}>
        <header>
          <SearchBar />
        </header>
        <ul>
          <Route
            path={`${match.path}/:category`}
            render={props => (
              <div>
                {console.log(props)}
                <Filter />
              </div>
            )}
          />
        </ul>
        <Pagination />
      </section>
    </div>
  );
};

export default AppList;
