import React, { useState, useEffect } from "react";
import styles from "./AppList.module.css";
import NavBar from "../../components/NavBar/NavBar";
import SearchBar from "../../components/SearchBar/SearchBar.jsx";
import Box from "../../components/Box/Box";
import Pagination from "../../components/Pagination/Pagination";
import NavLink from "../../components/NavLink/NavLink";
import data from "../../data/apps.json";

const sortBySumPriceAsc = appsArray => {
  return (
    appsArray &&
    appsArray.sort((a, b) => {
      let sumPriceA = 0;
      let sumPriceB = 0;
      a.subscriptions.map(subscription => (sumPriceA += subscription.price));
      b.subscriptions.map(subscription => (sumPriceB += subscription.price));
      return (sumPriceA > sumPriceB && 1) || sumPriceA === sumPriceB ? 0 : -1;
    })
  );
};

const filterByAppName = (app, name) => {
  if (app.name.length > 0) {
    return app.name.toLowerCase().includes(name.toLowerCase());
  } else return 1;
};

const FilteredApps = ({ apps }) => {
  //const [apps] = sortPriceAsc(filterByName(filterByCategory(useApps())));
  
  //apps.filter(app => app.name.toLowerCase().includes(name.toLowerCase()));
  return apps.length === 0 ? (
    <h1>No results were found!</h1>
  ) : (
    apps.map(app => (
      <Box
        key={app.id}
        title={app.name}
        description={app.description}
        tags={app.categories}
        subscriptions={app.subscriptions}
      ></Box>
    ))
  );
};

const useCategories = () => {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    setCategories(
      data
        .map(item => item.categories)
        .reduce((p, c) => [...p, ...c])
        .filter((item, i, arr) => arr.indexOf(item) === i)
        .sort()
    );
  }, []);
  return [categories];
};

let totalItems = 0; 

const useApps = (category, name, appsPerPage, currentPage) => {
  const [apps, setApps] = useState([]);
  const indexOfLastAppItem = currentPage * appsPerPage;
  const indexOfFirstAppItem = indexOfLastAppItem - appsPerPage;

  useEffect(() => {
    if (category) {
      setApps(
        data
          .sort(sortBySumPriceAsc(apps))
          .filter(app => {
            return (
              app.categories.includes(category) && filterByAppName(app, name)
            );
          })
          .slice(indexOfFirstAppItem, indexOfLastAppItem)
      );
    } else setApps(
             data
               .filter(app => filterByAppName(app, name))
               .slice(indexOfFirstAppItem, indexOfLastAppItem)
           );

    //setApps(currentAppItems);
  }, [category, name, indexOfFirstAppItem, indexOfLastAppItem]);
  return [apps, data.length];
};

const AppList = () => {
  const [categories] = useCategories();
  const [currentCategory, setCurrentCategory] = useState(null)
  const [searchFilter, setSearchFilter] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [apps,total] = useApps(currentCategory, searchFilter, 3, currentPage);

 console.log(Math.ceil(total / 3), apps.length);
  return (
    <div className={styles.container}>
      <NavBar title='Categories'>
        <NavLink
          onClick={() => {
            setCurrentCategory(null);
          }}
          name='All Categories'
          to={`/`}
          active={!currentCategory}
        ></NavLink>
        {categories &&
          categories.map((category, i) => (
            <NavLink
              onClick={() => {
                setCurrentCategory(category);
              }}
              name={category}
              active={currentCategory === category}
            ></NavLink>
          ))}
      </NavBar>

      <section className={styles.appList}>
        <header>
          <SearchBar onValueChange={setSearchFilter} />
        </header>
        <ul>
          {apps.map(app => (
            <Box
              key={app.id}
              title={app.name}
              description={app.description}
              tags={app.categories}
              subscriptions={app.subscriptions}
            ></Box>
          ))}
        </ul>
        <Pagination
          numberOfPages={total === 0 ? 1 : Math.ceil(total / 3)}
          activePage={currentPage}
          onPageChange={setCurrentPage}
        />
      </section>
    </div>
  );
};

export default AppList;
