import React from "react";
import styles from "./Pagination.module.css";

const Pagination = ({ itemsPerPage, totalItems, paginate, currentPage }) => {
  const pageNumbers = [];

  const maxPages = Math.ceil(totalItems / itemsPerPage);

  const fillPageNumbers = () => {
    for (let index = 1; index <= maxPages; index++) {
      pageNumbers.push(index);
    }
  };

  const PageNumber = ({ currentPage, paginate, number }) => { 
    return (<li key={number} className={currentPage === number ?  styles.active : ""}>
      <a onClick={paginate} href="!#">{number}</a>
    </li>)
  }

  const createPagination = () => { 
    fillPageNumbers();
    return pageNumbers && pageNumbers.map(number => (
      <PageNumber currentPage={currentPage} pageNumber={paginate} number={number}/>
    ))
  }

  return (
    <ul className={styles.pagination}>
      <li>
        <a href="!#">&lt;</a>
      </li>
      {createPagination}
      <li>
        <a href="!#">&gt;</a>
      </li>
    </ul>
  );
};

export default Pagination;
