import React, { useState, useEffect } from "react";
import styles from "./AppList.module.css";
import NavBar from "../../components/NavBar/NavBar";
import SearchBar from "../../components/SearchBar/SearchBar.jsx";
import Box from "../../components/Box/Box";
import Pagination from "../../components/Pagination/Pagination";
import NavLink from "../../components/NavLink/NavLink";
import { Switch, useRouteMatch, useParams, Route } from "react-router-dom";
import data from "../../data/apps.json";

const sortBySumPriceAsc = appsArray => {
  return appsArray && appsArray.sort((a, b) => {
    let sumPriceA = 0;
    let sumPriceB = 0;
    a.subscriptions.map(subscription => (sumPriceA += subscription.price));
    b.subscriptions.map(subscription => (sumPriceB += subscription.price));
    return (sumPriceA > sumPriceB && 1) || sumPriceA === sumPriceB ? 0 : -1;
  });
};

const filterByAppName = (appsArray, name) => {
  return appsArray && appsArray.filter(f => {
    if (name.length > 0) {
      return f.name.toLowerCase().includes(name.toLowerCase());
    } else return 1;
  });
};

const FilteredApps = ({ category, name }) => {
  //const [apps] = sortPriceAsc(filterByName(filterByCategory(useApps())));
  const [apps] = useApps(category);

  sortBySumPriceAsc(apps);
  filterByAppName(apps, name);

  return (
    apps &&
    apps.filmap(app => (
      <Box
        key={app.id}
        title={app.name}
        description={app.description}
        tags={app.categories}
        subscriptions={app.subscriptions}
      ></Box>
    ))
  );
};

const useCategories = () => {
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    setCategories(
      data
        .map(item => item.categories)
        .reduce((p, c) => [...p, ...c])
        .filter((item, i, arr) => arr.indexOf(item) === i)
        .sort()
    );
  }, []);
  return [categories];
};

const useApps = category => {
  const [apps, setApps] = useState([]);
  useEffect(() => {
    category
      ? setApps(data.filter(app => app.categories.includes(category)))
      : setApps(data);
  }, [category]);

  console.log(category);
  return [apps];
};

const AppList = () => {
  const match = useRouteMatch();
  const params = useParams();
  const [categories] = useCategories();
  const [searchFilter, setSearchFilter] = useState("");
  return (
    <div className={styles.container}>
      <NavBar title="Categories">
        <NavLink name="All Categories" to={`${match.path}`}></NavLink>
        {categories &&
          categories.map((category, i) => (
            <NavLink
              key={i}
              name={category}
              to={`${match.path}${category}`}
            ></NavLink>
          ))}
      </NavBar>

      <section className={styles.appList}>
        <header>
          <SearchBar onValueChange={setSearchFilter} />
        </header>
        <ul>
          <Switch>
            <Route
              path={`${match.path}:category`}
              render={props => (
                <FilteredApps
                  category={props.match.params.category}
                  name={searchFilter}
                />
              )}
            />
            <Route
              path={`${match.path}`}
              render={props => <FilteredApps name={searchFilter} />}
            />
          </Switch>
        </ul>
        <Pagination />
      </section>
    </div>
  );
};

export default AppList;
